import React, { useState } from 'react';
import ReactDOM from 'react-dom/client';
import './index.css';
import './styling.scss';
import reportWebVitals from './reportWebVitals';


function TodoList() {
  const [task, addTask] = useState("");
  const [todos, setTodos] = useState([]);

  function Adding() {
    const myResult = (
      <div>
        <input type="text" className='result-input' value={task} />
      </div>
    ); 
    const root = ReactDOM.createRoot(document.getElementById('root'));
    root.render(myResult);
  }
  const addTodo = () => {
    setTodos((t) => [...t, "New Todo"]);
  };




  return (
    <>
    <div className="List">
      <h1>ВАШ ПЕРЕЛІК ЗАВДАНЬ</h1>
      <input type="text" className='main-input' value={task} onChange={(e) => addTask(e.target.value)} />
      <Todos todos={todos} addTodo={addTodo} />

      <button onClick={Adding}>ДОДАТИ</button>
      <br />
      <br />
      <div className='tasks'>
        <h2>booo</h2>
        <h2>booo</h2>
        <h2>booo</h2>
        <h2>booo</h2>
      </div>
    </div>
    </>
  );
}
const root = ReactDOM.createRoot(document.getElementById('root'));
root.render(
  <React.StrictMode>
    <TodoList />
  </React.StrictMode>
);

// If you want to start measuring performance in your app, pass a function
