import React, { useState } from 'react';
import ReactDOM from 'react-dom/client';
import './index.css';
import './styling.scss';
import reportWebVitals from './reportWebVitals';


function TodoList() {
  const [task, addTask] = useState("");


    function setTask () {
      addTask({task});
    };

  function Adding() {
    const myResult = (
      <div>
        <input type="text" className='result-input' value={task} />
      </div>
    ); 
    const root = ReactDOM.createRoot(document.getElementById('root'));
    root.render(myResult);
  }





  return (
    <div className="List">
      <h1>ВАШ ПЕРЕЛІК ЗАВДАНЬ</h1>
      <input type="text" className='main-input' value={task} onChange={setTask} />
      <button onClick={Adding}>ДОДАТИ</button>
      <br />
      <br />
      <div className='tasks'>
        <h2>booo</h2>
        <h2>booo</h2>
        <h2>booo</h2>
        <h2>booo</h2>
      </div>
    </div>
  );
}
const root = ReactDOM.createRoot(document.getElementById('root'));
root.render(
  <React.StrictMode>
    <TodoList />
  </React.StrictMode>
);

