import React, { useState, useEffect } from 'react';
import ReactDOM from 'react-dom/client';
import './index.css';
import './styling.scss';
import reportWebVitals from './reportWebVitals';


function TodoList() {
  const [tasks, addTask] = useState("");
  const [getTasks, displayTasks] = useState("");

  useEffect(() => {
    displayTasks(() => tasks + "bobo");
  }, [tasks]);







  return (
    <div className="List">
      <h1>ВАШ ПЕРЕЛІК ЗАВДАНЬ</h1>
      <input type="text" className='main-input' value={tasks}/>
      <button onClick={(e) => addTask(e.target.value)}>ДОДАТИ</button>
      <br />
      <br />
      <div className='tasks'>
        {getTasks}
      </div>
    </div>
  );
}
const root = ReactDOM.createRoot(document.getElementById('root'));
root.render(
  <React.StrictMode>
    <TodoList />
  </React.StrictMode>
);

// If you want to start measuring performance in your app, pass a function
